import React from "react";
import { ScrollView,Text,StyleSheet,View,Image,ImageBackground } from 'react-native';

const image = { uri: "https://raw.githubusercontent.com/anny881104/horus/master/assets/wall.png" };


// Make a component
const UserScreen = ({ navigation }) => {
  return (
   <View style={{backgroundColor:"#CBA72F"}}>
      <ImageBackground source={image} style={styles.bgimage}>

     
      <View style={styles.userbox}>
        <Image style={{ width: 60, height: 60,borderRadius:15 }}
              source={require('../assets/userpic.png')}
        />
        <Text style={styles.username}>KABA</Text>
      </View>
      <View style={styles.infobox}>
        <Text style={styles.username}>帳戶</Text>
      </View>
      </ImageBackground>  
   </View>
  

    
  );
}
const styles = StyleSheet.create({
      userbox:{ 
        marginTop:87,
        alignItems:"center",
      },
      username:{
        fontSize: 20,
        marginTop:20,
        fontWeight:"bold", 
      },
      bgimage:{
        height:643,
        width:389,
        marginTop:40,
        marginLeft:15,
      },
});

export default UserScreen;
