import React from "react";
import { ScrollView,Text,StyleSheet,View,Image,ImageBackground } from 'react-native';

const image = { uri: "https://raw.githubusercontent.com/anny881104/horus/master/assets/wall.png" };
import { ListItem } from 'react-native-elements';

// Make a component
const UserScreen = ({ navigation }) => {
  return (
   <View style={{backgroundColor:"#CBA72F"}}>
      <ImageBackground source={image} style={styles.bgimage}></ImageBackground>  

    <ScrollView style={{paddingTop: 44}} >
      <View style={styles.userbox}>
        <Text style={styles.word}>KABA</Text>
        <ListItem
              title="Notifications"
            />
            <ListItem
              title="Profile"
            />
            <ListItem
              title="Password"
            />
            <ListItem
              title="Sign Out"
              rightIcon={{ name: 'cancel' }}
            />
    </View>
    </ScrollView>
    
   </View>
  

    
  );
}
const styles = StyleSheet.create({
    box:{
        flex:1, 
        marginTop:200,
        alignItems:"center",
        justifyContent:"center",
      },
      word:{
          fontSize: 15,
          fontWeight:"bold",
        },
        bgimage:{
            height:643,
            width:389,
            marginTop:25,
            marginLeft:15,
        },
});

export default UserScreen;
