import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Ionicons from 'react-native-vector-icons/Ionicons';
import { Image } from 'react-native';

import AlbumScreen from './src/screens/AlbumScreen';
import DetailScreen from './src/screens/DetailScreen';
import SettingsScreen from './src/screens/SettingsScreen';
import MeScreen from './src/screens/MeScreen';
import albumData from "./src/json/albums.json";


import JournalScreen from './screen/JournalScreen';
import KnowledgeScreen from './screen/KnowledgeScreen';
import UserScreen from "./screen/UserScreen";



const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();

const JournalStack = () => {
  return (
      <Stack.Navigator>
        <Stack.Screen 

          component={JournalScreen} 

        />
        <Stack.Screen 
          name="Detail" 
          component={DetailScreen}
          options={({ route }) => ({ 
            title: route.params.title,
            headerStyle: {
              backgroundColor: '#4F9DEB',
            },
            headerTintColor: '#fff',
            headerTitleStyle: {
              fontWeight: '400',
              fontSize: 20
            },  
           })}
        />      
      </Stack.Navigator>
  );
}


const App = () => {
  return (
    <NavigationContainer >
      <Tab.Navigator 
        screenOptions={({ route }) => ({
          tabBarIcon: ({ focused, color, size }) => {
            let iconPath;

            if (route.name === 'Journal') {
              iconPath = focused
              ? require('./assets/1.png'):
              require('./assets/1.png');
            } else if (route.name === 'Knowledge') {
              iconPath = focused
              ? require('./assets/2.png'):
              require('./assets/2.png');
            } else if (route.name == 'User') {
              iconPath = focused
              ? require('./assets/3.png'):
              require('./assets/3.png');
            }

            // You can return any component that you like here!
            return (
              <Image 
                style={{width: 35, height: 35}}
                source={iconPath} 
              />
            );
          },
        })}
        tabBarOptions={{
          activeTintColor: '#CBA72F',
          inactiveTintColor: '#212122',
          labelStyle: {
            fontSize: 11,
            marginTop: 0,
            padding: 0,
          },
        }}
      >
        <Tab.Screen name="Journal" component={JournalStack} />
        <Tab.Screen name="Knowledge" component={KnowledgeScreen} />
        <Tab.Screen name="User" component={UserScreen} />
      </Tab.Navigator>
    </NavigationContainer>
  );
}

export default App;
